package gluePaP.glue.lfg;
import gnu.prolog.io.*;
import gnu.prolog.term.*;

import java.io.*;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.ArrayList;
import java.util.List;

/**
 * This class reads in a prolog f-structure file as generated by XLE and
 * creates lexical entries. For the derivation of lexical entries the XLE
 * transfer system is used.
 */
public class FStructureParser {
    ArrayList<Term> termList = new ArrayList<>();


    public FStructureParser(Path filepath) {
        try {
            List<String> lines = Files.readAllLines(filepath);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }





}
